option.value:
@echo off
setlocal enabledelayedexpansion

set "CONFIG_FILE=%~dp0java_config.txt"

:: Read JAVA_HOME from config file
if not exist "%CONFIG_FILE%" (
    echo ❌ Config file not found at: "%CONFIG_FILE%"
    exit /b 1
)

set "JAVA_HOME="
for /f "usebackq delims=" %%i in ("%CONFIG_FILE%") do (
    set "JAVA_HOME=%%i"
    goto :found
)

:found
if not exist "!JAVA_HOME!\bin" (
    echo ❌ Java bin folder not found at "!JAVA_HOME!\bin"
    exit /b 1
)

:: Generate wrapper scripts for all Java tools
echo Creating command wrappers for Java tools in "!JAVA_HOME!\bin"...
for %%f in ("!JAVA_HOME!\bin\*.exe") do (
    set "TOOL=%%~nxf"
    >"%~dp0%%~nf.cmd" (
        echo @echo off
        echo "!JAVA_HOME!\bin\%%~nxf" %%*
    )
    echo ✅ Created wrapper: %%~nf.cmd
)

echo Done.
exit /b 0
